import React, { useState, useEffect } from 'react';

function StockData() {
  const [symbol, setSymbol] = useState('AAPL'); // Default stock symbol (Apple Inc.)
  const [interval, setInterval] = useState('daily'); // Default time interval
  const [stockData, setStockData] = useState(null);
  const [loading, setLoading] = useState(false);

  const apiKey = '565BBFBEFMPCFMLO';
  const apiUrl = `https://www.alphavantage.co/query?function=TIME_SERIES_${interval.toUpperCase()}_ADJUSTED&symbol=${symbol}&apikey=${apiKey}`;

  const fetchData = async () => {
    setLoading(true);

    try {
      const response = await fetch(apiUrl);
      const data = await response.json();

      // Alpha Vantage organizes data by date, so you can access it easily.
      setStockData(data['Time Series (Daily)']); // Adjust the key if using a different interval.

      setLoading(false);
    } catch (error) {
      console.error('Error fetching stock data:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [symbol, interval]);

  const handleSymbolChange = (e) => {
    setSymbol(e.target.value);
  };

  const handleIntervalChange = (e) => {
    setInterval(e.target.value);
  };

  return (
    <div>
      <h2>Stock Data</h2>
      <div>
        <label>
          Stock Symbol:
          <input type="text" value={symbol} onChange={handleSymbolChange} />
        </label>
      </div>
      <div>
        <label>
          Time Interval:
          <select value={interval} onChange={handleIntervalChange}>
            <option value="daily">Daily</option>
            <option value="weekly">Weekly</option>
            <option value="monthly">Monthly</option>
          </select>
        </label>
      </div>
      {loading ? (
        <p>Loading stock data...</p>
      ) : stockData ? (
        <div>
          {/* Display stock data here */}
          {/* You can map through the stockData object and render it as needed */}
          {Object.entries(stockData).map(([date, values]) => (
            <div key={date}>
              Date: {date}, Close Price: {values['4. close']}
            </div>
          ))}
        </div>
      ) : (
        <p>No stock data available.</p>
      )}
    </div>
  );
}

export default StockData;
